/**
 * Creates an object with the same values as `object` and keys generated by
 * running each own enumerable string keyed property of `object` thru
 * `iteratee`. The iteratee is invoked with three arguments:
 * (value, key, object).
 *
 * @param {Object} object The object to iterate over.
 * @param {Function} iteratee The function invoked per iteration.
 * @returns {Object} Returns the new mapped object.
 */
export const mapKeys = (object, iteratee) => {
  if (object == null || typeof object !== 'object') {
    return {};
  }
  const result = {};
  for (const key in object) {
    if (Object.prototype.hasOwnProperty.call(object, key)) {
      result[iteratee(object[key], key, object)] = object[key];
    }
  }
  return result;
};